import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate, useNavigate, useLocation } from 'react-router-dom';
import LoginForm from './components/auth/LoginForm.jsx';
import RegisterForm from './components/auth/RegisterForm.jsx';
import SellerDashboard from './components/dashboard/SellerDashboard.jsx';
import BuyerDashboard from './components/dashboard/BuyerDashboard.jsx';
import AdminDashboard from './components/dashboard/AdminDashboard.jsx';
import LandingPage from './components/layout/LandingPage.jsx';
import './styles/App.css';

function App() {
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [showRegister, setShowRegister] = useState(false);
  const [showLogin, setShowLogin] = useState(false);
  const [showLanding, setShowLanding] = useState(true);
  const [user, setUser] = useState(null);

  useEffect(() => {
    // Check if user is already logged in
    const token = localStorage.getItem('token');
    const userData = localStorage.getItem('user');

    if (token && userData) {
      try {
        const parsedUser = JSON.parse(userData);
        setUser(parsedUser);
        setIsAuthenticated(true);
        setShowLanding(false);
      } catch (error) {
        console.error('Failed to parse user data:', error);
        localStorage.removeItem('token');
        localStorage.removeItem('user');
      }
    }
  }, []);

  const handleAuthSuccess = () => {
    const userData = localStorage.getItem('user');
    if (userData) {
      setUser(JSON.parse(userData));
      setIsAuthenticated(true);
      setShowLanding(false);
      setShowLogin(false);
      setShowRegister(false);
    }
  };

  const handleShowLogin = () => {
    setShowLogin(true);
    setShowRegister(false);
    setShowLanding(false);
  };

  const handleShowRegister = () => {
    setShowRegister(true);
    setShowLogin(false);
    setShowLanding(false);
  };

  const handleBackToLanding = () => {
    setShowLanding(true);
    setShowLogin(false);
    setShowRegister(false);
  };

  if (isAuthenticated && user) {
    return <Dashboard />;
  }

  if (showLanding) {
    return <LandingPage onShowLogin={handleShowLogin} onShowRegister={handleShowRegister} />;
  }

  return (
    <div className="App">
      <div className="auth-container">
        <div className="auth-header">
          <h1>EthioSoft Marketplace</h1>
          <p>A digital marketplace for Ethiopian software developers</p>
          <button onClick={handleBackToLanding} className="back-to-landing">
            ‚Üê Back to Home
          </button>
        </div>

        {showRegister ? (
          <RegisterForm 
            onSuccess={handleAuthSuccess}
            onSwitchToLogin={handleShowLogin}
          />
        ) : (
          <LoginForm 
            onSuccess={handleAuthSuccess}
            onSwitchToRegister={handleShowRegister}
          />
        )}
      </div>
    </div>
  );
}

export default App;
